import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import GetName from "./getName";

interface Album {
  id: number;
  artist_id: number;
  name: string;
  description: string;
  cover: string;
  cover_small: string;
  release_date: number;
  popularity: number;
}

function AlbumList() {
  const [allAlbums, setAllAlbums] = useState<Album[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [error, setError] = useState<string | null>(null);

  const limit = 10;

  const fetchAllAlbums = async () => {
    try {
      const response = await fetch(`http://localhost:8000/albums`);
      if (!response.ok) {
        throw new Error(`Erreur HTTP: ${response.status}`);
      }
      const data = await response.json();
      console.log("Réponse de l'API:", data);
      setAllAlbums(data);
      setTotalPages(Math.ceil(data.length / limit));
    } catch (error) {
      console.error("Erreur:", error);
      if (error instanceof Error) {
        setError(error.message);
      } else {
        setError("Une erreur inconnue s'est produite.");
      }
    }
  };

  useEffect(() => {
    fetchAllAlbums();
  }, []);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const startIndex = (currentPage - 1) * limit;
  const endIndex = startIndex + limit;
  const albums = allAlbums.slice(startIndex, endIndex);

  if (error) return <div>Erreur: {error}</div>;
  if (!albums || albums.length === 0) return <div>Aucun album trouvé.</div>;

  return (
    <div>
      <h1>Liste des albums - Page {currentPage}</h1>
      <div className="flex text-white bg-gray-800 p-4">
        {albums.map((album) => (
          <div key={album.id}>
            <Link to={`/album/${album.id}`}>
              <h2>{album.name}</h2>
            </Link>
            <Link to={`/artists/${album.artist_id}`} key={album.artist_id}>
              <GetName id={album.artist_id} />
            </Link>
            <p>{album.description}</p>
            <img src={album.cover} alt={`Couverture de ${album.name}`} />
          </div>
        ))}
      </div>

      <div>
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Précédent
        </button>
        <span>
          {" "}
          Page {currentPage} sur {totalPages}{" "}
        </span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Suivant
        </button>
      </div>
    </div>
  );
}

export default AlbumList;
