import React, { useEffect, useState } from "react";
import ArtistsItem from './ArtistsItem';

interface Artist {
  id: number;
  name: string;
  description: string;
  bio: string;
  photo: string;
}

function ArtistsList () {
  const [allArtists, setAllArtists] = useState<Artist[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [error, setError] = useState<string | null>(null);

  const limit = 10;

  const fetchAllArtists = async () => {
    try {
      const response = await fetch(`http://localhost:8000/artists`);
      if (!response.ok) {
        throw new Error(`Erreur HTTP: ${response.status}`);
      }
      const data = await response.json();
      setAllArtists(data);
      setTotalPages(Math.ceil(data.length / limit));
    } catch (error) {
      console.error("Erreur:", error);
      if (error instanceof Error) {
        setError(error.message);
      } else {
        setError("Une erreur inconnue s'est produite.");
      }
    }
  };

  useEffect(() => {
    fetchAllArtists();
  }, []);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const startIndex = (currentPage - 1) * limit;
  const endIndex = startIndex + limit;
  console.log(allArtists)
  const artists = allArtists.slice(startIndex, endIndex);

  if (error) return <div>Erreur: {error}</div>;
  if (artists.length === 0) return <div>Aucun artistes trouvé.</div>;

  return (
    <div key={currentPage} className="text-white flex flex-col">
      <h2>Liste des artistes - Page {currentPage}</h2>
      <div className="flex flex-wrap">
        {artists.map((artist) => (
            <div
            key ={artist.id}
            className="w-[250px] p-2 px-3 rounded hover:bg-[#ffffff26] flex flex-wrap"
          >
              <ArtistsItem
              id={artist.id}
              name={artist.name}
              description={artist.description}
              bio={'Voir plus ...'}
              photo={artist.photo}
              />  
            </div>
          ))}  
      </div>
        
        <div className="self-center">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Précédent
        </button>
        <span>
          {" "}
          Page {currentPage} sur {totalPages}{" "}
        </span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Suivant
        </button>
      </div>
    </div>
  );
}

export default ArtistsList;
