import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import GetName from "./getName";
import Navbar from "./Navbar";

interface Album {
  id: number;
  artist_id: number;
  name: string;
  description: string;
  cover: string;
  cover_small: string;
  release_date: number;
  popularity: number;
}

interface Tracks {
  id: number;
  album_id: number;
  name: string;
  mp3: string;
  duration: number;
}

function AlbumDetail() {
  const { id } = useParams<{ id: string }>();
  const [album, setAlbum] = useState<Album | null>(null);
  const [tracks, setTracks] = useState<Tracks[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchAlbumDetail = async () => {
      try {
        const response = await fetch(`http://localhost:8000/albums/${id}`);
        if (!response.ok) {
          throw new Error(`Erreur HTTP: ${response.status}`);
        }
        const data = await response.json();
        console.log("Détails de l'album:", data);

        setAlbum(data.album || data);
        setTracks(data.tracks || data);

        console.log("Tracks:", data.tracks);
      } catch (error) {
        console.error("Erreur:", error);
        if (error instanceof Error) {
          setError(error.message);
        } else {
          setError("Une erreur inconnue s'est produite.");
        }
      }
    };

    fetchAlbumDetail();
  }, [id]);

  if (error) return <div>Erreur: {error}</div>;
  if (!album) return <div>Chargement...</div>;
  if (!tracks) return <div>Pas de musiques trouvé...</div>;

  return (
    <>
      <div className=" mb-4 text-white">
        <h1 className="my-5 font-bold text-2xl">Charts</h1>
        <div className="flex overflow-auto">
          <div className="min-w-[180px] p-2 px-3 rounded cursor-pointer hover:bg-[#ffffff26]">
            <img src={album.cover} alt="Image album" className="rounded" />
            <p className="font-bold mt-2 mb-1">{album.name}</p>
            <Link to={`/artists/${album.artist_id}`} key={album.artist_id}>
              <GetName id={album.artist_id} />
            </Link>
            <p className="text-slate-200 text-sm">{album.description}</p>
            <p className="mt-1">
              <img
                src="/images/spotify_logo.png"
                alt="Spotify logo"
                className="inline-block w-5"
              />
              <b> Spotify</b> • Date de sortie:{" "}
              {new Date(album.release_date * 1000).toLocaleDateString()}
              <b> • Popularité: {album.popularity}</b>
            </p>
          </div>
        </div>
        {tracks.map((track) => (
          <div className="min-w-[180px] p-2 px-3 rounded cursor-pointer hover:bg-[#ffffff26]">
            <img
              src={album.cover_small}
              alt="Image album"
              className="rounded w-20"
            />
            <p className="font-bold mt-2 mb-1">{track.name}</p>
            <p className="text-slate-200 text-sm">{track.duration} min</p>
          </div>
        ))}
      </div>
    </>
  );
}

export default AlbumDetail;
