import { usePlayer } from ""; // Importer le contexte

function AlbumDetail() {
  const { playTrack } = usePlayer(); // Utiliser le playTrack du contexte

  // Le reste de ton code pour AlbumDetail...
  // Remplacer la partie de l'ancienne logique pour jouer la musique par `playTrack`

  const handleTrackClick = (trackUrl: string) => {
    playTrack(trackUrl); // Utiliser la fonction playTrack pour démarrer la musique
  };

  return (
    <div className="flex flex-wrap text-white m-4">
      {tracks.map((track) => (
        <div
          key={track.id}
          className="w-[250px] p-2 px-3 rounded cursor-pointer hover:bg-[#ffffff26]"
          onClick={() => handleTrackClick(track.mp3)} // On click, jouer la musique
        >
          <img
            src={album.cover_small}
            alt="Image album"
            className="w-20 rounded"
          />
          <p className="font-bold mt-2 mb-1">{track.name}</p>
          <p className="text-slate-200 text-sm">{track.duration} min</p>
        </div>
      ))}
    </div>
  );
}

export default AlbumDetail;
