import React, { useEffect, useState } from "react";
import ArtistsItem from './ArtistsItem';
import { useParams } from "react-router-dom";

interface Artist {
  id: number;
  name: string;
  description: string;
  bio: string;
  photo: string;
}

function ArtistsSearch () {
  const { name } = useParams<{ name: string }>();
  let query: string;
  if (!name) query = `http://localhost:8000/artists`;
  else query = `http://localhost:8000/search?query=${name}&type=artist`;
  const [allArtists, setAllArtists] = useState<Artist[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [error, setError] = useState<string | null>(null);

  const limit = 10;

  const fetchAllArtists = async () => {
    try {
      const response = await fetch(query);
      if (!response.ok) {
        throw new Error(`Erreur HTTP: ${response.status}`);
      }
      const data = await response.json();
      setAllArtists(data.artists);
      setTotalPages(Math.ceil(data.artists.length / limit));
    } catch (error) {
      console.error("Erreur:", error);
      if (error instanceof Error) {
        setError(error.message);
      } else {
        setError("Une erreur inconnue s'est produite.");
      }
    }
  };

  useEffect(() => {
    fetchAllArtists();
  }, []);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const startIndex = (currentPage - 1) * limit;
  const endIndex = startIndex + limit;
  const artists = allArtists.slice(startIndex, endIndex);

  if (error) return <div>Erreur: {error}</div>;
  if (artists.length === 0) return <div>Aucun artistes trouvé.</div>;

  return (
    <div key={currentPage}>
      <h2>Recherche de {name} - Page {currentPage}</h2>  
        {artists.map((artist) => (
            <ArtistsItem
            id={artist.id}
            name={artist.name}
            description={artist.description}
            bio={artist.bio}
            photo={artist.photo}
            />  
          
        ))}
        <div>
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Précédent
        </button>
        <span>
          {" "}
          Page {currentPage} sur {totalPages}{" "}
        </span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Suivant
        </button>
      </div>
    </div>
  );
}

export default ArtistsSearch;
