import { Link } from "react-router-dom";

interface ArtistsItemProps {
    id: number;
    name: string;
    description: string;
    bio: string;
    photo: string;
}

const ArtistItem: React.FC<ArtistsItemProps> = ({ id, name, description, bio, photo }) => {

  return (
    <>
            <Link to={`/artists/${id}`}>
            <div>
              <img src={photo} alt={`Image de l'artiste ${name}`} className="h-[150px]"/>  
            </div>
            <div>
              <h2>{name}</h2>
              <p>{description}</p>
              <p className="text-sm">{bio}</p>  
            </div>
            
            </Link>
    </>
  );
};

export default ArtistItem;
