import { createContext, useRef, useState } from "react";
import { ReactNode } from "react";

const songsData = [
  {
    id: 0,
    name: "Song One",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "3:00",
  },
  {
    id: 1,
    name: "Song Two",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "2:20",
  },
  {
    id: 2,
    name: "Song Three",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "2:32",
  },
  {
    id: 3,
    name: "Song Four",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "2:50",
  },
  {
    id: 4,
    name: "Song Five",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "3:10",
  },
  {
    id: 5,
    name: "Song Six",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "2:45",
  },
  {
    id: 6,
    name: "Song Seven",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "2:18",
  },
  {
    id: 7,
    name: "Song Eight",
    image: "http://he3.magnatune.com/music/Adam Fielding/Pieces (extended)/cover_200.jpg",
    file: "http://he3.magnatune.com/all/05-While%20Stars%20Still%20Burn-Adam%20Fielding.mp3",
    desc: "Put a smile on your face with these happy tunes",
    duration: "2:35",
  },
];

export const PlayerContext = createContext({
  audioRef: { current: null },
  seekBar: { current: null },
  seekBg: { current: null },
  track: songsData,
  setTrack: () => {},
  playStatus: false,
  setPlayStatus: () => {},
  time: {
    currentTime: {
      second: 0,
      minute: 0,
    },
    totalTime: {
      second: 0,
      minute: 0,
    },
  },
  setTime: () => {},
  play: () => {},
  pause: () => {},
});

interface PlayerContextProviderProps {
  children: ReactNode;
}

const PlayerContextProvider = (props: PlayerContextProviderProps) => {
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const seekBg = useRef<HTMLDivElement | null>(null);
  const seekBar = useRef<HTMLDivElement | null>(null);

  const [track, setTrack] = useState(songsData[0]);
  const [playStatus, setPlayStatus] = useState(false);
  const [time, setTime] = useState({
    currentTime: {
      second: 0,
      minute: 0,
    },
    totalTime: {
      second: 0,
      minute: 0,
    },
  });

  const play = () => {
    if (audioRef.current) {
      audioRef.current.play();
      setPlayStatus(true);
    }
  };

  const pause = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      setPlayStatus(false);
    }
  };

  const contextValue = {
    audioRef,
    seekBar,
    seekBg,
    track,
    setTrack,
    playStatus,
    setPlayStatus,
    time,
    setTime,
    play,
    pause,
  };

  // return (
  //   <PlayerContext.Provider value={contextValue}>
  //     {props.children}
  //   </PlayerContext.Provider>
  // );
};

export default PlayerContextProvider;
